<?php    if(!defined('_PS_VERSION_')){    exit;}include_once(_PS_MODULE_DIR_.'moduledemo/controllers/ModuleDemo.php');//use PrestaShop\PrestaShop\Core\Module\WidgetInterface;class ModuleDemo extends Module{    protected $_html = '';    protected $templateFile;    public function __construct()    {        $this->name = 'moduledemo';        $this->tab = 'front_office_feature';        $this->version = '1.0';        $this->author = 'Stepan Kakosyan';        $this->ps_versions_compliancy = array('min' => '1.5', 'max' => _PS_VERSION_);        $this->need_instance = 0;        $this->bootstrap = true;        $this->confirmUninstall = $this->l('Are you sure you want to uninstall?');        parent::__construct();//            $this->confirmUninstall = $this->l('Are you sure you want to uninstall?');        $this->displayName = $this->l('Module Demo');        $this->description = $this->l('Admin can choose shipping fee by zip (postal) code of customer');        $this->templateFile = 'module:moduledemo/views/template/hook/moduledemo.tpl';    }    public function install()    {        if (!parent::install() ||            !$this->registerHook('displayHome') ||            !$this->registerHook('displayLeftColumn') ||            !$this->registerHook('displayHeader')        ) {            return false;        } else {            return true;        }    }    public function getContent()    {        $html = '';        if (Tools::isSubmit('submitDemo')) {            Configuration::updateValue('DEMO_ADV_TITLE', Tools::getValue('demo_title'));            Configuration::updateValue('DEMO_ADV_DESC', Tools::getValue('demo_desc'));            Configuration::updateValue('DEMO_ADV_IMAGE', Tools::getValue('demo_image'));            $html .= $this->displayConfirmation($this->l('Թարմացված են'));        }        $html .= '              <form action="' . $_SERVER['REQEST_URU'] . '" method="post" class="defaultForm form-horizontal">                  <div class="panel">                        <div class=panel-heading>' . $this->l('Կարգավորումներ') . '</div>                        <div class=form-group>                                               <div style="width: 40%; min-height:450px; border:3px solid black;float:left"> <img  src="image/'.Configuration::get('DEMO_ADV_IMAGE').'" alt="ddd"></div>                                                   <label class="control-laber col-lg-3">' . $this->l('Ներբեռնիր պատկեր') . '</label>                            <div class="col-lg-6">                                 <input  type="file" name="demo_image"  value="' . Configuration::get('DEMO_ADV_IMAGE') . '"/>                            </div><br><br><br>                            <label class="control-laber col-lg-3">' . $this->l('Վերնագիր') . '</label>                            <div class="col-lg-6">                                 <input  type="text" name="demo_title" value="' . Configuration::get('DEMO_ADV_TITLE') . '">                            </div><br><br><br>                            <label class="control-laber col-lg-3">' . $this->l('Նկարագրություն') . '</label>                            <div class="col-lg-6">                                <input type="text" name="demo_desc" value="' . Configuration::get('DEMO_ADV_DESC') . '">                            </div><br><br><br>                                                     <input style="float: left;padding: 25px;font-size: 150%;position: absolute;right:45px; bottom:45px;" type="submit" name="submitDemo" value="' . $this->l('Պահպանել') . '" class="btn btn-default" >                        </div>              </div>                 </form>            ';        return $html;    }    public function hookdisplayHeader($params)    {        $this->context->controller->registerStylesheet('modules-moduledemo', 'modules/' . $this->name . '/css/moduledemo.css', ['media' => 'all', 'priority' => 150]);    }    public function hookdisplayLeftColumn($params){        $title = Configuration::get("DEMO_ADV_TITLE");        $desc = Configuration::get('DEMO_ADV_DESC');        $image = Configuration::get('DEMO_ADV_IMAGE');        $this->context->smarty->assign(            array(                'image' => $image,                'title' => $title,                'desc' => $desc,            )        );        return $image = $this->display(__FILE__, 'moduledemo.tpl');    }    }?>